void ustaw(float _x, float _y) { cout << "\x1b[" << int(_y) << ";" << int(_x) << "H";  }
void czysc() { cout << "\x1b[2J" << "\x1b[3J";}
void drawstate(void) 
{
    //cout << "\x1b[2J";                 // czyscekran
    ustaw(0,0);
    cout << "                                                       ";
    ustaw(0,0);
    cout << "HP: " << HP << "   Punkty: " << int(Punkty);
    ustaw(0,HEIGHT+1);    
    cout << "---------------------";
    //cout << "cios = " << cios << endl;
    ustaw(0,HEIGHT);
    cout << "                    ";
    // rysuj gracz
    ustaw(WIDTH-x,HEIGHT-y);
    cout << "\x1b[47m\x1b[30m";            // koloruj(...)
    if(cios==0) cout << " ";
    if(cios==1) cout << "X";
    cout << "\x1b[0m"<<endl;              // czyscformat()
    // rysuj przeciwnika
    ustaw(WIDTH-px,HEIGHT);
    cout << "\x1b[41m";            // koloruj(...)
    cout << "*";
    cout << "\x1b[0m"<<endl;              // czyscformat()

    cout << endl << endl; 
    cout << "\x1b[0m";              // czyscformat()
    cout << endl;
    ustaw(0,0);
}


